generated.css:
@import url("https://fonts.googleapis.com/css2?family=Ubuntu:ital,wght@0,300;0,400;0,500;0,700;1,300;1,400;1,500;1,700&display=swap");
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  width: 100%;
  height: 100%;
  font-family: "Ubuntu", sans-serif;
  font-size: 14px;
  overflow-x: hidden;
}

.container {
  display: block;
  max-width: 1196px;
  padding: 0 12px;
  margin: 0 auto;
  height: 100%;
}

ul {
  list-style: none;
}

img {
  max-width: 100%;
  height: auto;
}

sup, sub {
  font-size: 0.75rem;
  height: 100%;
}

h2 {
  text-align: center;
  font-size: 1.4rem;
  text-transform: uppercase;
  font-weight: 900;
  margin: 2rem 0 1rem 0;
  color: #444;
}
@media (min-width: 769px) {
  h2 {
    text-align: left;
    font-size: 2.4rem;
  }
}

.glider,
.glider-contain {
  margin: 0 auto;
  position: relative;
}

.glider,
.glider-track {
  transform: translateZ(0);
}

.glider-dot,
.glider-next,
.glider-prev {
  border: 0;
  padding: 0;
  user-select: none;
  outline: 0;
}

.glider-contain {
  width: 100%;
}

.glider {
  overflow-y: hidden;
  -webkit-overflow-scrolling: touch;
  -ms-overflow-style: none;
}

.glider-track {
  width: 100%;
  margin: 0;
  padding: 0;
  display: flex;
  z-index: 1;
}

.glider.draggable {
  user-select: none;
  cursor: grab;
}

.glider.draggable .glider-slide img {
  user-select: none;
  pointer-events: none;
}

.glider.drag {
  cursor: grabbing;
}

.glider-slide {
  user-select: none;
  justify-content: center;
  align-content: center;
  width: 100%;
  min-width: 150px;
}

.glider-slide img {
  max-width: 100%;
}

.glider::-webkit-scrollbar {
  opacity: 0;
  height: 0;
}

.glider-next,
.glider-prev {
  position: absolute;
  background: 0 0;
  z-index: 2;
  font-size: 1.2rem;
  font-weight: 100;
  text-decoration: none;
  left: -0.5rem;
  top: 50%;
  transform: translateY(-50%);
  padding: 1rem;
  cursor: pointer;
  color: #444;
  opacity: 1;
  border-radius: 50%;
  line-height: 1;
  transition: opacity 0.5s cubic-bezier(0.17, 0.67, 0.83, 0.67), color 0.5s cubic-bezier(0.17, 0.67, 0.83, 0.67), 0.5s cubic-bezier(0.17, 0.67, 0.83, 0.67);
}

.glider-next:focus,
.glider-next:hover,
.glider-prev:focus,
.glider-prev:hover {
  color: #83d0bd;
  background-color: rgba(68, 68, 68, 0.2);
}

.glider-next {
  right: -0.5rem;
  left: auto;
}

.glider-next.disabled,
.glider-prev.disabled {
  opacity: 0.25;
  color: #666;
  cursor: default;
}

.glider-hide {
  opacity: 0;
}

.glider-dots {
  user-select: none;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin: 1rem auto 0;
  padding: 0;
}

.glider-dot {
  display: block;
  cursor: pointer;
  color: #ccc;
  border-radius: 20px;
  background: #868686;
  width: 14px;
  height: 14px;
  margin: 7px;
}

.glider-dot:focus,
.glider-dot:hover {
  background: #83d0bd;
}

.glider-dot.active {
  background: #83d0bd;
  border-color: #338772;
}

@media (max-width: 36em) {
  .glider::-webkit-scrollbar {
    opacity: 1;
    -webkit-appearance: none;
    width: 7px;
    height: 3px;
  }

  .glider::-webkit-scrollbar-thumb {
    opacity: 1;
    border-radius: 99px;
    background-color: rgba(156, 156, 156, 0.25);
    -webkit-box-shadow: 0 0 1px rgba(255, 255, 255, 0.25);
    box-shadow: 0 0 1px rgba(255, 255, 255, 0.25);
  }
}
.count {
  background-color: #f48400;
}
.count .new-counter {
  padding: 0.2rem 0;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
}
@media (max-width: 768px) {
  .count .new-counter {
    flex-direction: column;
  }
}
.count .new-counter h4 {
  text-transform: uppercase;
  font-size: 0.75rem;
  font-weight: 100;
}
.count .new-counter-btn {
  color: white;
  font-size: 0.65rem;
  padding: 0.4rem;
  text-transform: uppercase;
  text-decoration: 1px underline white;
  transition: 0.3s ease-in background-color;
}
.count .new-counter-btn:hover, .count .new-counter-btn:focus {
  background-color: rgba(0, 0, 0, 0.2);
}
@media (max-width: 768px) {
  .count .new-counter-btn {
    padding: 0 0 0.3rem 0;
  }
}
.count .new-counter .counter__wrapper {
  display: flex;
  justify-content: flex-end;
  align-items: center;
}
.count .new-counter .counter__wrapper li {
  text-transform: uppercase;
  padding: 0.5rem;
  font-size: 0.75rem;
}
.count .new-counter .counter__wrapper li span {
  padding: 0 0.2rem;
  display: inline-block;
  font-weight: 900;
}

.category {
  margin-bottom: 3rem;
}
@media (min-width: 769px) {
  .category__wrapper {
    display: flex;
    justify-content: center;
    align-items: center;
  }
}
.category__wrapper-item {
  position: relative;
  margin-bottom: 1rem;
}
@media (min-width: 769px) {
  .category__wrapper-item {
    margin-right: 1rem;
  }
  .category__wrapper-item:last-child {
    margin-right: 0;
  }
}
.category__wrapper-item .style-it {
  position: absolute;
  bottom: 3.4rem;
  width: 100%;
  left: 0;
  z-index: 2;
}
.category__wrapper-item .style-it .btn {
  display: inline-block;
  text-transform: uppercase;
  text-decoration: none;
  font-size: 0.75rem;
  color: white;
  position: absolute;
  bottom: 1rem;
  left: 0;
  z-index: 4;
  padding: 0.5rem 1rem;
  background-color: #83d0bd;
  transition: 0.3s ease-in background-color;
}
.category__wrapper-item .style-it .btn:hover {
  background-color: #444;
}
.category__wrapper-item .content {
  position: absolute;
  bottom: -3rem;
  width: 100%;
  margin: 0;
  z-index: 3;
  padding-bottom: 3rem;
}
.category__wrapper-item .content .btn {
  display: block;
  text-align: center;
  color: white;
  margin: 0;
  width: 100%;
  background-color: #444;
  padding: 1rem 0;
  text-decoration: none;
  text-transform: uppercase;
  font-size: 1.6rem;
  font-weight: 500;
  transition: 0.3s ease-in background-color;
}
.category__wrapper-item .content .btn:hover {
  background-color: #777777;
}

.mixitup {
  padding: 0.1rem 0;
  background-color: #e5e5e5;
}

.container .mix {
  padding: 0;
}
.container .mix-menu {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
}
.container .mix-li {
  position: relative;
  padding: 0.5rem 1rem 0.5rem 0;
  font-size: 0.899rem;
  text-transform: uppercase;
  cursor: pointer;
}
.container .mix-li:focus, .container .mix-li:hover {
  opacity: 0.8;
}
.container .mix-li::after {
  content: "";
  position: absolute;
  bottom: -3px;
  left: 0;
  width: 0;
  height: 4px;
  transform: scale(0);
  transform-origin: right;
  transition: transform 0.4s ease-in-out;
}
.container .mix-li.active {
  position: relative;
  color: #3a9982;
  opacity: 1;
}
.container .mix-li.active::after {
  content: "";
  position: absolute;
  bottom: -3px;
  left: 0;
  width: calc(100% - 1rem );
  transform: scale(1);
  transform-origin: left;
  height: 3px;
  background-color: #5ec2a9;
}
.container .mix-item {
  cursor: pointer;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-gap: 2px;
  place-items: center;
  padding: 1rem 0;
}
@media (min-width: 769px) {
  .container .mix-item {
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 4px;
  }
}
.container .mix-item li {
  transform-origin: top;
  transition: transform 0.3s ease, height 0.3s ease;
  background-color: white;
  padding: 1rem 1rem 2rem 1rem;
  cursor: pointer;
}
@media (max-width: 768px) {
  .container .mix-item li {
    padding: 0.5rem 0.5rem 1rem 0.5rem;
  }
}
.container .mix-item li.activated {
  overflow: hidden;
  transform: translateX(0) scale(1);
  width: 100%;
  height: auto;
  transition: box-shadow 0.3s ease, transform 0.3s ease;
}
.container .mix-item li.activated:hover {
  box-shadow: 0 1px 6px rgba(134, 134, 134, 0.5);
  transform: translateY(-8px);
  width: 100%;
}
.container .mix-item li.deleted {
  transform: translateX(-100px) scale(0);
  height: 0;
  padding: 0;
}
.container .mix-item li h3 {
  color: #444;
  font-size: 1.2rem;
  padding: 0.2rem 0;
}
.container .mix-item li p {
  padding: 0.2rem 0;
  color: #868686;
}
.container .mix-item li p b {
  color: orange;
  font-size: 1.4rem;
}

@keyframes main {
  0% {
    transform: scaleX(0);
  }
  100% {
    transform: scaleX(1);
  }
}
.header {
  height: 120px;
  width: 100%;
}
.header__wrapper {
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 100%;
}
.header__wrapper .menu-toggle {
  cursor: pointer;
  display: grid;
  place-items: center;
  background-color: #ececec;
  width: 50px;
  height: 50px;
  border-radius: 50%;
}
.header__wrapper .menu-toggle .line {
  position: relative;
  display: inline-block;
  width: 1.2rem;
  height: 2px;
  background-color: #444;
}
.header__wrapper .menu-toggle .line::after, .header__wrapper .menu-toggle .line::before {
  content: "";
  position: absolute;
  width: 1.2rem;
  height: 2px;
  background-color: #444;
}
.header__wrapper .menu-toggle .line::before {
  width: 1.2rem;
  top: -6px;
  left: 0;
}
.header__wrapper .menu-toggle .line::after {
  width: 1.2rem;
  bottom: -6px;
  left: 0;
}
@media (min-width: 769px) {
  .header__wrapper .menu-toggle {
    display: none;
  }
}
.header__wrapper .menu-toggle:hover {
  background-color: #cdece4;
}
.header__logo {
  display: flex;
  justify-content: center;
  align-items: center;
  color: #444;
  font-size: 2rem;
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: 5px;
  font-style: italic;
}
@media (max-width: 768px) {
  .header__logo {
    padding-left: 1rem;
  }
}
.header__logo p {
  padding-right: 1rem;
}
.header__ul {
  display: flex;
  justify-content: flex-start;
  align-items: center;
}
@media (max-width: 768px) {
  .header__ul {
    display: none;
  }
}
.header__ul.header__user-cart {
  display: flex;
}
.header__ul-li {
  padding: 0 0.8rem;
}
.header__ul-li-links {
  display: inline-block;
  text-decoration: none;
  color: #535353;
  font-size: 0.75rem;
  font-weight: 100;
  text-transform: uppercase;
  position: relative;
  transition: 0.3s ease color;
}
.header__ul-li-links::after {
  content: "";
  position: absolute;
  bottom: -55px;
  left: 0;
  height: 2px;
  width: 0;
  background-color: #83d0bd;
  transition: 0.3s ease width;
}
.header__ul-li-links.postureshirt::before, .header__ul-li-links.basket::before {
  content: "%";
  color: white;
  background: orange;
  position: absolute;
  top: -1.4rem;
  right: -1rem;
  width: 25px;
  height: 25px;
  display: grid;
  place-items: center;
  font-size: 0.88rem;
  border-radius: 50%;
}
.header__ul-li-links.user, .header__ul-li-links.basket {
  font-size: 1.5rem;
  margin-top: -0.8rem;
}
.header__ul-li-links.basket::before {
  content: "2";
  top: -0.8rem;
}
.header__ul-li-links:hover {
  color: #83d0bd;
}
.header__ul-li-links:hover::after {
  width: 100%;
}

.hero__wrapper {
  position: relative;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center center;
  min-height: 400px;
  z-index: 1;
}
@media (max-width: 768px) {
  .hero__wrapper {
    background-position: 72%;
  }
}
.hero__wrapper:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.05);
  z-index: -1;
}
@media (max-width: 768px) {
  .hero__wrapper:before {
    background-color: rgba(0, 0, 0, 0.3);
  }
}
.hero__content {
  position: relative;
  text-align: left;
  padding: 10rem 0;
  width: 50%;
  z-index: 2;
}
@media (max-width: 768px) {
  .hero__content {
    text-align: center;
    width: 100%;
    padding: 4rem 1rem;
  }
}
.hero__content .sale {
  display: inline-block;
  color: #f48400;
  text-transform: uppercase;
  font-size: 1.2rem;
  font-weight: 100;
  padding-bottom: 0.5rem;
}
.hero__content-title {
  font-size: 3rem;
  color: #6d6d6d;
  text-transform: uppercase;
}
@media (max-width: 768px) {
  .hero__content-title {
    font-size: 2rem;
    color: white;
  }
}
.hero__content-text {
  color: #6d6d6d;
  line-height: 1.8;
  letter-spacing: 2px;
  font-size: 1rem;
  font-weight: 500;
  padding: 2rem 0;
}
@media (max-width: 768px) {
  .hero__content-text {
    font-size: 0.75rem;
    color: white;
  }
}
.hero__content-btn {
  position: relative;
  cursor: pointer;
  display: inline-block;
  padding: 1.5rem 3rem;
  background: #83d0bd;
  color: white;
  text-decoration: none;
  text-transform: uppercase;
  font-size: 1rem;
  opacity: 1;
  z-index: 12;
  transition: 300ms ease-in box-shadow, 250ms ease-in transform, opacity 0.3s ease-in, background-color 0.3s ease-in;
}
@media (max-width: 768px) {
  .hero__content-btn {
    padding: 1rem 2rem;
  }
}
.hero__content-btn:focus, .hero__content-btn:hover {
  box-shadow: 0 1px 6px rgba(134, 134, 134, 0.4);
  transform: translateY(-4px);
  opacity: 0.8;
  background-color: #444;
}

.relief {
  margin: 3rem 0;
}
.relief__wrapper-btns {
  padding: 0;
  width: 100%;
}
@media (max-width: 768px) {
  .relief__wrapper-btns {
    background-color: #f7f7f7;
  }
}
.relief__wrapper-btns-btn {
  cursor: pointer;
  display: inline-block;
  padding: 1rem 2.6rem;
  background: #bbb;
  text-transform: uppercase;
  font-weight: 900;
  color: #444;
}
@media (max-width: 768px) {
  .relief__wrapper-btns-btn {
    font-size: 0.66rem;
    padding: 1rem 0.555rem;
  }
}
.relief__wrapper-btns-btn.active {
  color: white;
  background: #7fcfbb;
}
.relief__wrapper-btns-btn.active:nth-of-type(2) {
  background: #7fcfbb;
  margin: -4px;
}
.relief__wrapper-btns-btn.active:nth-of-type(3) {
  background: #7fcfbb;
  margin: -2px;
}
.relief__wrapper-btns-btn:nth-of-type(2) {
  background: #ccc;
  margin: -4px;
}
.relief__wrapper-btns-btn:nth-of-type(3) {
  background: #eee;
  margin: -2px;
}
.relief__wrapper-content-img {
  display: none;
}
.relief__wrapper-content-img.active {
  display: flex !important;
  justify-content: flex-start;
  align-items: center;
  padding: 0.4rem;
  background: #7fcfbb;
}
@media (max-width: 768px) {
  .relief__wrapper-content-img.active {
    flex-direction: column;
  }
}
.relief__wrapper-content-img.active:last-child {
  padding: 0 0 0 0.4rem;
}
@media (max-width: 768px) {
  .relief__wrapper-content-img.active:last-child {
    padding: 0.4rem;
  }
}
.relief__wrapper-content-text {
  padding: 2rem 4rem;
}
@media (max-width: 768px) {
  .relief__wrapper-content-text {
    padding: 2rem 1rem;
  }
}
.relief__wrapper-content-text h3 {
  font-size: 1.2rem;
  padding: 1rem 0;
  text-transform: uppercase;
  color: #fff;
}
.relief__wrapper-content-text p {
  font-size: 1rem;
  font-weight: 100;
  color: #f7f7f7;
  line-height: 1.5;
}
.relief__wrapper-content-text-btn {
  cursor: pointer;
  display: inline-block;
  margin-top: 1rem;
  padding: 1.2rem 2.4rem;
  background-color: #444;
  color: white;
  text-transform: uppercase;
  text-decoration: none;
  box-shadow: 0px 1px 6px #868686;
  opacity: 1;
  transition: 0.3s ease-in box-shadow, 0.3s ease-in opacity;
}
.relief__wrapper-content-text-btn:focus, .relief__wrapper-content-text-btn:hover {
  box-shadow: none;
  opacity: 0.8;
}

.posture {
  background-color: #e5e5e5;
  margin: 3rem 0;
}
@media (max-width: 768px) {
  .posture {
    display: none;
  }
}
.posture__figure {
  position: relative;
  width: 100%;
  display: block;
  text-align: center;
  z-index: 1;
}
.posture__figure-img {
  max-width: 400px;
  width: 100%;
}
.posture__figure-circle {
  cursor: pointer;
  position: absolute;
  top: 6%;
  left: 53%;
  color: white;
  display: grid;
  place-items: center;
  font-size: 0.75rem;
  width: 30px;
  height: 30px;
  background: tomato;
  border-radius: 50%;
  border: 6px solid rgba(241, 32, 32, 0.322);
  z-index: 10;
}
.posture__figure-circle.show {
  background: #83d0bd;
  border: 6px solid rgba(131, 208, 189, 0.322);
}
.posture__figure-circle.show::after {
  background: #83d0bd;
}
.posture__figure-circle:nth-of-type(2) {
  top: 23%;
  left: 45%;
}
.posture__figure-circle:nth-of-type(3) {
  top: 36%;
  left: 38%;
}
.posture__figure-circle:nth-of-type(4) {
  top: 48%;
  left: 48%;
}
.posture__figure-circle:nth-of-type(5) {
  top: 78%;
  left: 48%;
}
.posture__figure-circle::after {
  content: "";
  position: absolute;
  width: 32px;
  height: 32px;
  background: tomato;
  border-radius: 50%;
  animation: pulse 2000ms infinite;
  z-index: -1;
}
.posture__content {
  position: relative;
  z-index: 1;
}
.posture__content h2 {
  padding: 1rem 0 2rem 0;
}
.posture__content-infoText {
  position: absolute;
  top: 10rem;
  left: 1rem;
  max-width: 200px;
  line-height: 1.6;
  color: #868686;
}
.posture__content-infoText .tags {
  position: relative;
  cursor: pointer;
  display: grid;
  place-items: center;
  width: 52px;
  height: 52px;
  font-size: 0.75rem;
  border-radius: 50%;
  background-color: #f2faf8;
}
.posture__content-infoText .tags:hover {
  background-color: #a8ded1;
}
.posture__content-infoText .tags:hover::after {
  background: #83d0bd;
  border-radius: 5px;
  bottom: -34px;
  color: #fff;
  content: attr(title);
  left: 48%;
  padding: 5px 15px;
  position: absolute;
  z-index: 98;
  width: 350px;
}
.posture__content-infoText .tags:hover::before {
  border: solid;
  border-color: #83d0bd transparent;
  border-width: 0 8px 8px 8px;
  bottom: -8px;
  content: "";
  left: 50%;
  position: absolute;
  z-index: 99;
}
.posture__content-item {
  position: absolute;
  transform-origin: left;
  background: white;
  box-shadow: 2px 2px 12px #ccc;
  width: 300px;
  padding: 3rem 2rem;
  transform: scale(0) translateY(-150%);
  opacity: 0;
  z-index: 10;
  border-radius: 15px;
  transition: 0.3s ease-in transform, 0.4s ease-in opacity;
}
.posture__content-item.show {
  transform: scale(1) translateY(0);
  opacity: 1;
  z-index: 1;
}
.posture__content-item.content-1 {
  top: -20%;
  left: 5%;
}
@media (min-width: 769px) {
  .posture__content-item.content-1 {
    top: 18%;
    left: 60%;
  }
}
.posture__content-item.content-2 {
  top: -20%;
  left: 5%;
}
@media (min-width: 769px) {
  .posture__content-item.content-2 {
    top: 32%;
    left: 55%;
  }
}
.posture__content-item.content-3 {
  top: -20%;
