.trustpilot,
.trustpilot-contain {
  margin: 0 auto;
  position: relative;
}

.trustpilot,
.trustpilot-track {
  transform: translateZ(0);
}

.trustpilot-dot,
.trustpilot-next,
.trustpilot-prev {
  border: 0;
  padding: 0;
  user-select: none;
  outline: 0;
}

.trustpilot-contain {
  width: 100%;
}

.trustpilot {
  overflow-y: hidden;
  -webkit-overflow-scrolling: touch;
  -ms-overflow-style: none;
}

.trustpilot-track {
  width: 100%;
  margin: 0;
  padding: 0;
  display: flex;
  z-index: 1;
}

.trustpilot.draggable {
  user-select: none;
  cursor: grab;
}

.trustpilot.draggable .trustpilot-slide img {
  user-select: none;
  pointer-events: none;
}

.trustpilot.drag {
  cursor: grabbing;
}

.glider-slide,
.trustpilot-slide {
  user-select: none;
  justify-content: center;
  align-content: center;
  width: 100%;
  min-width: 150px;
}

.glider-slide img,
.trustpilot-slide img {
  max-width: 100%;
}

.trustpilot::-webkit-scrollbar {
  opacity: 0;
  height: 0;
}

.trustpilot-next,
.trustpilot-prev {
  position: absolute;
  background: 0 0;
  z-index: 2;
  font-size: 1.2rem;
  font-weight: 100;
  text-decoration: none;
  left: -0.5rem;
  top: 50%;
  transform: translateY(-50%);
  padding: 1rem;
  cursor: pointer;
  color: #444;
  opacity: 1;
  border-radius: 50%;
  line-height: 1;
  transition: opacity 0.5s cubic-bezier(0.17, 0.67, 0.83, 0.67), color 0.5s cubic-bezier(0.17, 0.67, 0.83, 0.67), 0.5s cubic-bezier(0.17, 0.67, 0.83, 0.67);
