.trustpilot-next:focus,
.trustpilot-next:hover,
.trustpilot-prev:focus,
.trustpilot-prev:hover {
  color: #83d0bd;
  background-color: rgba(68, 68, 68, 0.2);
}

.trustpilot-next {
  right: -0.5rem;
  left: auto;
}

.trustpilot-next.disabled,
.trustpilot-prev.disabled {
  opacity: 0.25;
  color: #666;
  cursor: default;
}

.trustpilot-hide {
  opacity: 0;
}

.trustpilot-dots {
  user-select: none;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin: 1rem auto 0;
  padding: 0;
}

