@import url('https://fonts.googleapis.com/css2?family=Ubuntu:ital,wght@0,300;0,400;0,500;0,700;1,300;1,400;1,500;1,700&display=swap');
$primary-clr: #83d0bd;
$secondary-clr: #868686;
$dark-gray: #444;
$accent: #f48400;

@mixin size($size) {
  @if $size == phone{
    @media (max-width: 768px) { @content; }
  } @else if $size == tablet {
    @media (min-width: 769px) { @content; }
  } @else if $size == desktop {
    @media (min-width: 1200px) { @content; }
  } @else if $size == desktoplg {
    @media (min-width: 1800px) { @content; }
  }
}
@mixin hovered($Y) {
	box-shadow: 0 1px 6px rgba($secondary-clr, 0.4);
	transform: translateY($Y);
}
//example mixin
// .header-title {  
// 	font-size: 2rem;  
// 	@include size(phone) {    
// 		 font-size: 1rem; 
// 	}
// }

@mixin btn($color, $hovered-color:null) {
	position: relative;
	cursor: pointer;
	display: inline-block;
	padding:1.5rem 3rem;
	background: $color;
	color: white;
	text-decoration: none;
	text-transform: uppercase;
	font-size: 1rem;
	opacity: 1;
	z-index: 12;
	transition: 300ms ease-in box-shadow, 250ms ease-in transform, opacity .3s ease-in, background-color .3s ease-in;
	@include size(phone){
	padding: 1rem 2rem;
	}
	&:focus,
	&:hover{
		@include hovered(-4px);
		opacity: 0.8;
		background-color: $hovered-color;
	}
}
//globals
*{
	box-sizing: border-box;
	margin: 0;
	padding: 0;
}
body{
	width: 100%;
	height:100%;
	font-family: 'Ubuntu', sans-serif;
	font-size: 14px;
	overflow-x:hidden;
}
.container{
	display: block;
	max-width: 1196px;
	padding:0 12px;
	margin: 0 auto;
	height: 100%;
}
ul{
	list-style: none;
}
img{
	max-width: 100%;
	height: auto;
}

sup, sub{
	font-size: .75rem;
	height: 100%;
}

h2{
	text-align: center;
	font-size: 1.4rem;
	text-transform: uppercase;
	font-weight: 900;
	margin: 2rem 0 1rem 0;
	color:#444;
	@include size(tablet){
		text-align: left;
		font-size: 2.4rem;
	}
}

