		letter-spacing: 5px;
		font-style: italic;
		@include size(phone){
			padding-left: 1rem;
		}
		p{
			padding-right: 1rem;
		}
	}
	&__ul{
		display: flex;
		justify-content: flex-start;
		align-items: center;
		@include size(phone){
			display: none;
		}
		&.header__user-cart{
			display: flex;
		}
		&-li{
			padding:0 0.8rem;
			&-links {
				display: inline-block;
				text-decoration: none;
				color: darken($secondary-clr,20%);
				font-size: .75rem;
				font-weight: 100;
				text-transform: uppercase;
				position: relative;
				transition: .3s ease color;
				&::after{
					content: "";
					position: absolute;
					bottom: -55px;
					left: 0;
					height: 2px;
					width: 0;
					background-color: $primary-clr;
					transition: .3s ease width;
				}
				&.postureshirt::before,&.basket::before{
					content: "%";
					color: white;
					background: orange; 
					position: absolute;
					top: -1.4rem;
					right:-1rem;
					width: 25px;
					height: 25px;
					display: grid;
					place-items: center;
