		}
		&-circle{
			cursor: pointer;
			position: absolute;
			top:6%;
			left:53%;
			color: white;
			display: grid;
			place-items: center;
			font-size: .75rem;
			width:30px;
			height: 30px;
			background: tomato;
			border-radius: 50%;
			border: 6px solid rgba(241, 32, 32, 0.322);
			z-index: 10;
			&.show{
				background: $primary-clr;
				border: 6px solid rgba($primary-clr, 0.322);
				&::after{
					background: $primary-clr;
				}
			}
			&:nth-of-type(2){
				top:23%;
				left:45%;
			}
			&:nth-of-type(3){
				top:36%;
				left:38%;
			}
			&:nth-of-type(4){
				top:48%;
				left:48%;

			}
			&:nth-of-type(5){
				top:78%;
				left:48%;


			}

			&::after{
				content: '';
				position: absolute;
				width:32px;
				height: 32px;
				background: tomato;
				border-radius: 50%;
				animation: pulse 2000ms infinite;
				z-index: -1;
			}
		}
	}
	&__content{
		position: relative;
		z-index: 1;
		h2{
			padding: 1rem 0 2rem 0;
		}
		&-infoText{
			position: absolute;
			top:10rem;
			left:1rem;
			max-width: 200px;
			line-height: 1.6;
			color:$secondary-clr;
			.tags{
				position: relative;
				cursor: pointer;
				display: grid;
				place-items: center;
				width: 52px;
				height: 52px;
				font-size: .75rem;
				border-radius: 50%;
				background-color: lighten($primary-clr, 30%);
				&:hover{
					background-color: lighten($primary-clr, 10%);
					&::after{
						background: $primary-clr;
						border-radius: 5px;
						bottom: -34px;
						color: #fff;
						content: attr(title);
						left: 48%;
						padding: 5px 15px;
						position: absolute;
						z-index: 98;
						width: 350px;
					}
					&::before{
						border: solid;
						border-color: $primary-clr transparent;
