				left:5%;
				@include size(tablet){
				top:33%;
				left:12%;
				}
			}
			&.content-4{
				top:-20%;
				left:5%;
				@include size(tablet){
				top:50%;
				left:54%;
				}
			}
			&.content-5{
				top:40%;
				left:5%;
				@include size(tablet){
				top:60%;
				left:12%;
				}
			}
			h3{
				text-transform: uppercase;
				font-size: 1.1rem;
				color: #444;
				padding-bottom: .6rem;
			}
			p{
				font-size: .85rem;
