		}
		&:hover{
			@include hovered(-2px);
			.arrow{
				color: $primary-clr;
			}
		}
	}
	&__content {
		padding: .5rem 0;
		.title{
			color:$dark-gray;
			text-transform: uppercase;
			font-weight: 100;
			font-size: 1rem;
			sup{
				font-size: .5rem;
			}
		}
		.variant{
			color: $secondary-clr;
			padding: .5rem 0;
		}
		.price{
			color: $accent;
			font-size: 1.2rem;
			&.prev{
				color: $dark-gray;
				text-decoration: solid red line-through;
			}
			&:nth-of-type(2){
				padding-left:2rem;
			}
		}
		.arrow{
			color: $secondary-clr;
			text-align: right;
			transition: .3s ease-in color;
		}
	}
}

.mission {
	padding: 1rem 0 2rem 0;
	&__wrapper{
		text-align: center;
		max-width: 60%;
		margin:0 auto;
		@include size(phone){
			max-width: 80%;
		}
		.title{
			text-transform: uppercase;
			font-size: 2.2rem;
			color: $dark-gray;
			padding-bottom: 1rem;
			@include size(phone){
				font-size: 1.4rem;
			}

		}
		p{
			line-height: 1.8;
			font-size: 1rem;
			color: darken($secondary-clr, 20%);
			padding-bottom: 1.6rem;
			@include size(phone){
				font-size: .9rem;
			}
		}
		.btn {
			@include btn($dark-gray);
		}
	}
}

sources.assets/css/_trustpilot.scss:
.trustpilot,
.trustpilot-contain {
    margin: 0 auto;
    position: relative
}

.trustpilot,
.trustpilot-track {
    transform: translateZ(0)
}

.trustpilot-dot,
.trustpilot-next,
.trustpilot-prev {
    border: 0;
    padding: 0;
    user-select: none;
    outline: 0
}

.trustpilot-contain {
    width: 100%
}

.trustpilot {
    overflow-y: hidden;
    -webkit-overflow-scrolling: touch;
    -ms-overflow-style: none
}

.trustpilot-track {
    width: 100%;
    margin: 0;
    padding: 0;
    display: flex;
    z-index: 1
}

.trustpilot.draggable {
    user-select: none;
    cursor: grab
}

.trustpilot.draggable .trustpilot-slide img {
    user-select: none;
    pointer-events: none
}

.trustpilot.drag {
    cursor: grabbing
}
.glider-slide,
.trustpilot-slide {
    user-select: none;
    justify-content: center;
    align-content: center;
    width: 100%;
    min-width: 150px
}
.glider-slide img, 
.trustpilot-slide img {
    max-width: 100%
}

.trustpilot::-webkit-scrollbar {
    opacity: 0;
    height: 0
}


.trustpilot-next,
.trustpilot-prev {
    position: absolute;
    background: 0 0;
    z-index: 2;
    font-size: 1.2rem;
    font-weight: 100;
    text-decoration: none;
    left:-0.5rem;
    top: 50%;
    transform: translateY(-50%);
    padding:1rem;
    cursor: pointer;
    color: $dark-gray;
    opacity: 1;
    border-radius: 50%;
    line-height: 1;
    transition: opacity .5s cubic-bezier(.17, .67, .83, .67), color .5s cubic-bezier(.17, .67, .83, .67), .5s cubic-bezier(.17, .67, .83, .67);
}

.trustpilot-next:focus,
.trustpilot-next:hover,
.trustpilot-prev:focus,
.trustpilot-prev:hover {
    color: $primary-clr;
    background-color: rgba($dark-gray, 0.2);
}

.trustpilot-next {
    right: -0.5rem;
    left: auto
}

.trustpilot-next.disabled,
.trustpilot-prev.disabled {
    opacity: .25;
    color: #666;
    cursor: default
}

.trustpilot-hide {
    opacity: 0
}

.trustpilot-dots {
    user-select: none;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    margin: 1rem auto 0;
    padding: 0
}

.trustpilot-dot {
    display: block;
    cursor: pointer;
    color: #ccc;
    border-radius: 5px;
    background: #ccc;
		border: 1px solid $secondary-clr;
    width: 36px;
    height: 6px;
    margin: 7px
}

.trustpilot-dot:focus,
.trustpilot-dot:hover {
    background: $primary-clr;

}

.trustpilot-dot.active {
    background: $primary-clr;
		border-color: darken($primary-clr, 30%);
}

@media(max-width:36em) {
    .trustpilot::-webkit-scrollbar {
        opacity: 1;
        -webkit-appearance: none;
        width: 7px;
        height: 3px
    }
    .trustpilot::-webkit-scrollbar-thumb {
        opacity: 1;
        border-radius: 99px;
        background-color: rgba(156, 156, 156, .25);
        -webkit-box-shadow: 0 0 1px rgba(255, 255, 255, .25);
        box-shadow: 0 0 1px rgba(255, 255, 255, .25)
    }
}

//custom styles

.trustpilot-main{
margin-top: 3rem;
    .trustpilot{
        padding: 1rem 0;
        &__single{
            position: relative;
            border: 1px solid rgba($secondary-clr, 0.1);
            padding: 1rem;
            text-decoration: none;
            color: $dark-gray;
            transition: .3s ease-in box-shadow, .3s ease-in transform;
            z-index: 3;
            &:hover{
                border: 1px solid rgba($secondary-clr, 0.1);
                box-shadow: 0 1px 6px rgba($secondary-clr, 0.4);
                transform: translateY(-8px);
            }
            blockquote{
                padding: 1rem;
                figure{
                    h4{
                        text-transform: uppercase;
                        padding: .6rem 0;
                    }
                    p{
                        line-height: 1.5;
                        font-weight: 100;
                        padding-bottom: 1rem;
                    }
                    .tpImage{
                        display: flex;
                        justify-content: space-between;
                        align-items: center;
                        figcaption{
                            font-style: italic;
                            color:$primary-clr;
                        }
                    }
                }
            }
            .quote {
                color: rgba($secondary-clr, 0.5);
                font-size: 2rem;
            }
        }
    }
    .tpImageLogo{
        cursor: pointer;
        text-align: center;
    }
}

sources.assets/css/_video.scss:
.video{
	background-color: #e5e5e5;
	padding: 2rem 0;

	&__wrapper{
		position: relative;

		&-play{
			cursor: pointer;
			position: absolute;
			top:50%;
			left:50.7%;
			transform: translate(-50%, -50%);
			background-color: $primary-clr;
			width:180px;
			height: 180px;
			display: grid;
			place-items: center;
			font-size: 3rem;
			padding-left: 1rem;
			color: white;
			border-radius: 50%;
			border: 22px solid lighten($primary-clr, 10%);
			z-index: 0;
			&::after{
				content: '';
				position: absolute;
				top: -42px;
				left: -42px;
				width: 220px;
				height: 220px;
				background-color: rgba($primary-clr, 0.9);
				border-radius: 50%;
				z-index: -1;
				animation: pulse 2000ms ease-in infinite;
				@include size(phone){
					top: -17px;
					left: -15px;
					width: 80px;
					height: 80px;
				}
			}
		@include size(phone){
			font-size: 1.2rem;
			width: 60px;
			height: 60px;
			padding-left: .4rem;
			border: 6px solid lighten($primary-clr, 10%);
		}
		}
	}
	.dots{
		.dots-png{
			opacity: 0.4;
			margin-top: -0.2rem;
			z-index: 0;
		}
	}
}

@keyframes pulse {
	0% {
		transform: scale(0.75);
		opacity: 0.75;
	}
	100% {
		transform:scale(1.5);
		opacity: 0;
	}
}

sources.assets/css/_infobar.scss:
.infobar{
	padding: 3rem 0;
	&__wrapper{
		display: grid;
		grid-template-columns: repeat(3, 1fr);
		@media(max-width:768px){
			grid-template-columns: repeat(1, 1fr);
		}
		&-single{
			position: relative;
			cursor: pointer;
			width: 100%;
			height: 100%;
			min-height: 220px;
			flex: 0 1 auto;
			padding: 1rem;
			background-color: white;
			border: 1px solid rgba($secondary-clr, 0.2);
			transition: box-shadow .3s ease-in, transform .3s ease-in;
			&:hover{
				box-shadow:0 2px 12px rgba($secondary-clr, 0.4);
				transform: translateY(-8px);
				transition: box-shadow .3s ease-in, transform .3s ease-in;
				.btn {
					background-color: $primary-clr;
					box-shadow: 0 2px 12px rgba($secondary-clr, 0.4);
				}
			}
			.icon{
				img{
					width: 100px;
					-webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
					filter: grayscale(100%);
				}
			}
			h3{
				color: $secondary-clr;
				padding:1rem 0 1.6rem 0;
			}
			p{
				line-height: 1.6;
				color:$dark-gray;
				font-size: .85rem;
				font-weight: 100;
				letter-spacing: 1px;
				padding-bottom: 2rem;

			}
			.btn{
			@include btn($dark-gray);
	}
	}
}
}

sources.assets/css/_footer.scss:
.footer{
	background-color: rgb(12, 24, 20);
	position: relative;
	padding-top: 2rem;
	.dots{
		padding-top: 1.5rem;
		.dots-png{
			opacity: 0.9;
		}
	}
	&__links{
		display: grid;
		grid-template-columns: repeat(4, 1fr);
		padding:1rem 0 5rem 0;
		@media(max-width:768px){
			grid-template-columns: repeat(2, 1fr);
		}
		&-section {
			h3{
				padding-bottom: 1rem;
				color: $primary-clr;
				font-size: 1.2rem;
				font-weight: 100;
				text-transform: uppercase;
			}
		}
		&-ul{
			list-style: none;

			&-li{
				&-a{
					display: block;
					color: darken($secondary-clr, 10%);
					text-decoration: none;
					font-weight: 100;
					font-size: .95rem;
					padding:.4rem;
					transition: .3s ease-in color;
					&:hover{
						color:white;
					}
				}
			}
		}
	}
	&__copy{
		position: relative;
		display: block;
		width: 100%;
		text-align: center;
		color:white;
		font-weight: 100;
		font-size: 0.75rem;
		letter-spacing: 2px;
		margin-top: 2rem;
		padding:2rem 0;
		&::after{
			content: '';
			position: absolute;
			top: 0;
			left: 50%;
			transform: translateX(-50%);
			width: 35%;
			height: 1px;
			background-color: rgba(lighten($primary-clr, 30%), 0.1);
		}
	}
}
