  position: relative;
  padding: 0.5rem 2rem 0.5rem 0;
  font-size: 1rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  cursor: pointer;
}
.container .mix-li:focus, .container .mix-li:hover {
  opacity: 0.8;
}
.container .mix-li::after {
  content: "";
  position: absolute;
  bottom: -3px;
  left: 0;
  width: 0;
  height: 4px;
  transform: scale(0);
  transform-origin: right;
  transition: transform 0.4s ease-in-out;
}
.container .mix-li.active {
  position: relative;
}
.container .mix-li.active::after {
  content: "";
  position: absolute;
  bottom: -2px;
  left: 0;
  width: 100%;
  transform: scale(1);
  transform-origin: left;
  height: 2px;
  background-color: #83d0bd;
}
.container .mix-item {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-gap: 5px;
  place-items: center;
